def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    ratios = item / bins
    priorities = np.zeros_like(bins)

    for i in range(len(bins)):
        if ratios[i] >= 2:
            priorities[i] = 0
        elif 1 < ratios[i] < 2:
            priorities[i] = np.exp(-np.power((ratios[i] - 1), 2))
        else:
            remaining_capacity = np.sum(bins) - bins[i]
            if remaining_capacity < item:
                priorities[i] = 0
            else:
                if item < 0.3 * bins[i]:
                    priorities[i] = 1 / ratios[i]
                else:
                    priorities[i] = np.sqrt(item / remaining_capacity)

    return priorities
