def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    """Highest priority if bin has exact capacity, otherwise pro-rated."""
    priorities = np.zeros(len(bins))

    for i, bin_capacity in enumerate(bins):
        if item == bin_capacity:
            priorities[i] = 1.0
        elif item < bin_capacity:
            priorities[i] = item / bin_capacity
        else:
            remaining_capacity = bin_capacity
            used_capacity = 0
            for j in range(i):
                used_capacity += bins[j]
            remaining_capacity -= used_capacity
            if item <= remaining_capacity:
                priorities[i] = 1.0
            else:
                priorities[i] = 1 - ((item - remaining_capacity) / (bins[i] - remaining_capacity))

    return priorities
