def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    """Further improved version of `priority_v1`.
    Now considering additional factors for priority calculation.
    """

    if np.all(item <= bins):
        return np.zeros_like(bins)

    priorities = np.zeros_like(bins)
    max_capacity = np.max(bins)
    min_capacity = np.min(bins)

    for i in range(len(bins)):
        if item > bins[i]:
            priorities[i] = 2 * (item - bins[i])  # Double the priority
        else:
            priorities[i] = bins[i] - item

    # Add an additional factor based on bin capacity ratio
    for i in range(len(bins)):
        normalized_capacity = (bins[i] - min_capacity) / (max_capacity - min_capacity)
        if normalized_capacity < 0.5:
            priorities[i] += 0.3 * (0.5 - normalized_capacity)  # Adjust priority based on capacity ratio
        else:
            priorities[i] += 0.2 * (normalized_capacity - 0.5)  # Adjust priority based on capacity ratio

    return priorities
