def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    priorities = np.zeros(len(bins))

    if len(bins) == 0:
        return priorities

    max_capacity = max(bins)
    min_capacity = min(bins)

    if item > max_capacity:
        msg = "Item size exceeds maximum bin capacity"
        raise ValueError(msg)

    bin_ranges = []
    current_range = [0, 0]

    for i in range(len(bins)):
        if bins[i] >= item:
            priorities[i] = 1
        else:
            if current_range[0] == 0:
                current_range[0] = i
            current_range[1] = i
            if i == len(bins) - 1 or bins[i] + bins[i+1] < item:
                bin_ranges.append(current_range)
                current_range = [0, 0]

    for start, end in bin_ranges:
        remaining_capacity = sum(bins[start:end+1])
        for j in range(end, len(bins)):
            remaining_capacity += bins[j]
            if bins[j] >= item and remaining_capacity >= item:
                priorities[j] = (j - start) + 1
                break

    return priorities
