def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    priorities = np.zeros(len(bins))

    if len(bins) == 0:
        return priorities

    total_capacity = sum(bins)

    if item > total_capacity:
        msg = "Item size exceeds total bin capacity"
        raise ValueError(msg)

    for i in range(len(bins)):
        remaining_capacity = total_capacity - sum(bins[:i])
        if bins[i] >= item and remaining_capacity >= item:
            priorities[i] = 1
            break
        elif i == len(bins) - 1:
            priorities[i] = -1
        else:
            for j in range(i+1, len(bins)):
                if bins[j] >= item:
                    temp_capacity = total_capacity - sum(bins[:j])
                    temp_sum = sum(bins[i:j+1])
                    for k in range(j, len(bins)):
                        temp_capacity += bins[k]
                        temp_sum += bins[k]
                        if bins[k] >= item and temp_sum >= item and temp_capacity >= item:
                            priorities[j] = (k - i) + 1
                            break
                        elif k == len(bins) - 1:
                            priorities[i] = -1
                            break
                if priorities[j] != 0:
                    break

    return priorities
