def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    priorities = np.zeros_like(bins)

    for i in range(len(bins)):
        remaining_capacity = bins[i]
        priority = 0.0

        if bins[i] >= item:
            priorities[i] = 1.0
        else:
            while remaining_capacity < item:
                if remaining_capacity < 0.25 * item:
                    priority += 0.2
                elif remaining_capacity < 0.5 * item:
                    priority += 0.4
                elif remaining_capacity < 0.75 * item:
                    priority += 0.6
                else:
                    priority += 0.8
                remaining_capacity += bins[i]

            if priority > 0.7:
                priorities[i] = priority * 1.8
            elif priority < 0.4:
                priorities[i] = priority * 0.6
            else:
                priorities[i] = priority

    return priorities
