def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    priorities = np.zeros_like(bins)

    for index, bin_capacity in enumerate(bins):
        if item <= bin_capacity:
            priorities[index] = 1.0
        else:
            remaining_capacity = bin_capacity - item
            if remaining_capacity > 0:
                priority = 1 / remaining_capacity
                if priority < 0.5:
                    priorities[index] = 0.9
                elif priority >= 0.5 and priority < 1.0:
                    priorities[index] = 0.7
                else:
                    if item > bin_capacity * 2:
                        priorities[index] = 0.3
                    else:
                        for i in range(index):
                            if bins[i] >= item:
                                priorities[index] = 0.6
                                break
                        else:
                            priorities[index] = 0.5
            else:
                if item >= bin_capacity * 2:
                    priorities[index] = 0.2
                else:
                    priorities[index] = 0.0

    return priorities
