def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    """Even more improved version of `priority_v1`."""
    priorities = np.zeros_like(bins)

    for i in range(len(bins)):
        ratio = item / bins[i]
        if bins[i] >= item:
            priorities[i] = 1.0
        elif bins[i] == 0:
            priorities[i] = 0.0
        else:
            if ratio < 0.1:
                priorities[i] = 0.1
            elif ratio >= 0.1 and ratio < 0.2:
                priorities[i] = 0.2
            elif ratio >= 0.2 and ratio < 0.3:
                priorities[i] = 0.3
            elif ratio >= 0.3 and ratio < 0.4:
                priorities[i] = 0.4
            elif ratio >= 0.4 and ratio < 0.5:
                priorities[i] = 0.5
            elif ratio >= 0.5 and ratio < 0.6:
                priorities[i] = 0.6
            elif ratio >= 0.6 and ratio < 0.7:
                priorities[i] = 0.7
            elif ratio >= 0.7 and ratio < 0.8:
                priorities[i] = 0.8
            elif ratio >= 0.8 and ratio < 0.9:
                priorities[i] = 0.9
            else:
                priorities[i] = 1.0

    return priorities
