def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    """Even more improved version of `priority_v1`."""
    priorities = np.zeros_like(bins)
    for i in range(len(bins)):
        if bins[i] >= item:
            priorities[i] = item / bins[i] + 0.1
        elif bins[i] >= item / 2:
            priorities[i] = 0.5 * item / bins[i] + 0.2
        else:
            count = 0
            while item > bins[i]:
                item -= bins[i]
                count += 1
            if count % 2 == 0:
                priorities[i] = 0.8 * count + item / bins[i] + 0.3
            else:
                priorities[i] = 0.7 * count + item / bins[i] + 0.3

    sorted_priorities = np.sort(priorities)[::-1]
    for j in range(len(priorities)):
        if priorities[j] == sorted_priorities[0]:
            priorities[j] += 0.5
        elif priorities[j] == sorted_priorities[-1]:
            priorities[j] -= 0.5

    min_bin_capacity = np.min(bins)
    max_bin_capacity = np.max(bins)

    for k in range(len(priorities)):
        if bins[k] == max_bin_capacity:
            priorities[k] += 1.0
        elif bins[k] == min_bin_capacity:
            priorities[k] -= 1.0

    return priorities
