def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    priorities = np.zeros_like(bins)

    max_bin_capacity = np.max(bins)

    for i in range(len(bins)):
        if bins[i] >= item:
            priorities[i] = 1.0
        elif bins[i] == 0:
            priorities[i] = 0.0
        else:
            remaining_capacity_ratio = (max_bin_capacity - bins[i]) / max_bin_capacity
            item_ratio = item / bins[i]

            if remaining_capacity_ratio < 0.2:
                priorities[i] = 0.2
            elif item_ratio < 0.25:
                priorities[i] = 0.3
            elif item_ratio >= 0.25 and item_ratio < 0.5:
                priorities[i] = 0.5
            elif item_ratio >= 0.5 and item_ratio < 0.75:
                priorities[i] = 0.7
            elif item_ratio >= 0.75 and item_ratio < 1:
                priorities[i] = 0.9
            else:
                priorities[i] = 1.0

    return priorities
