def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    """Improved version of `priority_v1`."""
    priorities = np.zeros_like(bins)
    for i in range(len(bins)):
        if bins[i] >= item:
            priorities[i] = item / bins[i] + 0.1
        elif bins[i] >= item / 2:
            priorities[i] = 0.5 * item / bins[i] + 0.2
        else:
            count = 0
            while item > bins[i]:
                item -= bins[i]
                count += 1
            if count % 2 == 0:
                priorities[i] = 1.3 * count + 0.7 * item / bins[i] + 0.4
            else:
                priorities[i] = 1.4 * count + 0.6 * item / bins[i] + 0.5

        if priorities[i] > 2.0:
            priorities[i] += 0.8
        elif priorities[i] > 1.5:
            priorities[i] += 0.6
        elif priorities[i] > 1.0:
            priorities[i] -= 0.4

        if bins[i] % 3 == 0:
            priorities[i] *= 1.2
        else:
            priorities[i] *= 1.1

        if i % 3 == 0:
            priorities[i] *= 1.6
        else:
            priorities[i] *= 1.4

    return priorities
