def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    priorities = np.zeros_like(bins)
    for i in range(len(bins)):
        if bins[i] >= item:
            priorities[i] = 1.0
        elif bins[i] == 0:
            priorities[i] = 0.0
        else:
            ratio = item / bins[i]
            if ratio < 0.2:
                priorities[i] = 0.1
            elif ratio < 0.5:
                priorities[i] = 0.3
            elif ratio < 0.8:
                priorities[i] = 0.6
            else:
                priorities[i] = 0.8
            if bins[i] % 2 == 0:
                priorities[i] += 0.05

        if i % 3 == 0:
            priorities[i] *= 1.3
        elif i % 2 == 0:
            priorities[i] *= 1.1
        else:
            priorities[i] *= 0.9
    return priorities
