def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    priorities = np.zeros_like(bins)
    for i in range(len(bins)):
        if bins[i] >= item:
            priorities[i] = 1.0
        elif bins[i] == 0:
            priorities[i] = 0.0
        else:
            ratio = item / bins[i]
            if ratio < 0.3:
                priorities[i] = 0.2
            elif ratio < 0.5:
                priorities[i] = 0.4
            elif ratio < 0.8:
                priorities[i] = 0.6
            elif ratio < 1.0:
                priorities[i] = 0.8
            else:
                priorities[i] = 1.0
        if i % 3 == 0:
            priorities[i] *= 1.5
        elif i % 3 == 1:
            priorities[i] *= 1.2
        else:
            priorities[i] *= 0.8
    return priorities
