def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    if item <= 0:
        raise ValueError("Item size must be greater than 0.")

    priorities = np.zeros_like(bins)
    for i in range(len(bins)):
        if bins[i] < item:
            priorities[i] = -1.0
        else:
            remaining_capacity = bins[i] - item
            if remaining_capacity > 0:
                priorities[i] = 1.0 / (1.0 + remaining_capacity) + 0.5
            else:
                priorities[i] = 2.0

            if i % 2 == 0:
                priorities[i] *= 1.5

            if item % 2 == 0:
                priorities[i] += 0.25

            if item > 5 and remaining_capacity < 10:
                priorities[i] += 1.0

            if item % 3 == 0:
                priorities[i] *= 2.0

            if i > 0:
                priorities[i] += priorities[i-1] / 3

            if item > 10 and bins[i] >= item * 2:
                priorities[i] += 2.0

            if i % 3 == 0:
                priorities[i] -= 0.5

            if item % 5 == 0:
                priorities[i] += 1.5

            if item > 15:
                if i % 4 == 0:
                    priorities[i] -= 0.25
                elif i % 4 == 1:
                    priorities[i] += 0.5
                elif i % 4 == 2:
                    priorities[i] -= 0.75
                else:
                    priorities[i] += 1.0

            if remaining_capacity % 2 == 0:
                priorities[i] *= 1.25

            if item < 3:
                priorities[i] -= 0.5

            if item > 15 and i % 2 == 0:
                priorities[i] += 1.0

            if item % 7 == 0:
                priorities[i] += 2.5

            if i % 5 == 0:
                priorities[i] -= 1.0

    return priorities
