def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    """Even more improved version of `priority_v1`."""
    priorities = np.zeros_like(bins)

    for i in range(len(bins)):
        if bins[i] >= item:
            if bins[i] == 0:
                priorities[i] = 0.0
            elif bins[i] == item:
                priorities[i] = 1.0
            else:
                priorities[i] = (item + bins[i]) / (2 * bins[i])
        else:
            priority_diff = (item - bins[i]) / item
            if priority_diff < 0.05:
                priorities[i] = 0.1
            elif priority_diff >= 0.05 and priority_diff < 0.25:
                priorities[i] = 0.3
            elif priority_diff >= 0.25 and priority_diff < 0.45:
                priorities[i] = 0.5
            elif priority_diff >= 0.45 and priority_diff < 0.65:
                priorities[i] = 0.7
            elif priority_diff >= 0.65 and priority_diff < 0.85:
                priorities[i] = 0.9
            else:
                priorities[i] = 0.95

    return priorities
