def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    ratios = item / bins
    priorities = np.zeros_like(bins)

    for i, ratio in enumerate(ratios):
        if ratio < 0.1:
            priorities[i] = 1 / (1 + np.exp(-30 * (ratio - 0.1))) + 0.1
        elif ratio >= 0.1 and ratio < 0.5:
            priorities[i] = 1 / (1 + np.exp(-15 * (ratio - 0.3))) + 0.3
        elif ratio >= 0.5 and ratio < 0.7:
            priorities[i] = 1 / (1 + np.exp(-10 * (ratio - 0.6))) + 0.4
        elif ratio >= 0.7 and ratio < 0.9:
            priorities[i] = 1 / (1 + np.exp(-5 * (ratio - 0.8))) + 0.5
        else:
            priorities[i] = 1 / (1 + np.exp(-2 * (ratio - 1))) + 0.6

        if i % 5 == 0:
            priorities[i] += 0.3
        else:
            for j in range(7):
                if i % 3 == 1:
                    priorities[i] -= 0.1 * j
                elif i % 4 == 1:
                    priorities[i] += 0.05 * j
                elif i % 5 == 1:
                    priorities[i] -= 0.02 * j
                else:
                    priorities[i] += 0.01 * j

    return priorities
