def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    priorities = np.zeros_like(bins)

    for i in range(len(bins)):
        if item <= bins[i]:
            if bins[i] % item == 0:
                priorities[i] = item / bins[i] + 0.5
            else:
                priorities[i] = item / bins[i]
        else:
            diff = item - bins[i]
            if diff > bins[i] / 2:
                priorities[i] = bins[i] / item
            else:
                priorities[i] = (bins[i] + diff) / item

    max_priority = np.max(priorities)
    min_priority = np.min(priorities)

    for j in range(len(bins)):
        if priorities[j] == max_priority:
            priorities[j] *= 1.2
        elif priorities[j] == min_priority:
            priorities[j] *= 0.8

    for k in range(len(bins)):
        if priorities[k] < 1.0:
            priorities[k] = priorities[k] ** 2
        else:
            priorities[k] = priorities[k] ** 0.5

    sum_priorities = np.sum(priorities)

    for m in range(len(bins)):
        priorities[m] = priorities[m] / sum_priorities

        if bins[m] % 2 == 0:
            priorities[m] += 0.1
        elif bins[m] % 3 == 0:
            priorities[m] += 0.2
        else:
            priorities[m] -= 0.1

        if bins[m] + item > 100:
            priorities[m] -= 0.3
        elif bins[m] + item < 50:
            priorities[m] += 0.3

    return priorities
