def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    priorities = np.zeros_like(bins)
    for i in range(len(bins)):
        if bins[i] == 0:
            priorities[i] = 0.0
        elif bins[i] < item:
            priorities[i] = 0.5
        else:
            ratio = bins[i] / item
            if ratio < 0.2:
                priorities[i] = 0.1
            elif ratio < 0.5:
                priorities[i] = 0.3
            elif ratio < 0.8:
                priorities[i] = 0.6
            else:
                priorities[i] = 0.8
            if bins[i] % 2 == 0:
                priorities[i] += 0.2
            if item % 2 == 0:
                priorities[i] += 0.1
            if item > bins[i] * 0.6:
                priorities[i] -= 0.4
    return priorities
