def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    priorities = np.zeros_like(bins)

    for i in range(len(bins)):
        if item < bins[i]:
            priorities[i] = item / bins[i]
        else:
            priorities[i] = 1.0

        if item > 3 * bins[i]:
            priorities[i] *= 2.0
        elif item > 2 * bins[i]:
            priorities[i] *= 1.5
        elif item > 1.5 * bins[i]:
            priorities[i] *= 1.2
        elif item > 1.2 * bins[i]:
            priorities[i] *= 1.1

        if priorities[i] < 0.5:
            if item < 0.5 * bins[i]:
                priorities[i] = 0.3
            elif item >= 0.5 * bins[i] and item < bins[i]:
                priorities[i] = 0.5
        elif priorities[i] > 1.5 and item < 4 * bins[i]:
            if item < 2 * bins[i]:
                priorities[i] = 1.7
            elif item >= 2 * bins[i] and item < 3 * bins[i]:
                priorities[i] = 1.9
            elif item >= 3 * bins[i] and item < 4 * bins[i]:
                priorities[i] = 1.95
        elif item >= 4 * bins[i]:
            priorities[i] = 2.0

        for j in range(len(bins)):
            if bins[j] % 2 == 0:
                priorities[j] += 0.1
            elif bins[j] % 3 == 0:
                priorities[j] += 0.2
            else:
                priorities[j] += 0.05

    return priorities
