def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    priorities = np.zeros_like(bins)

    for i in range(len(bins)):
        if bins[i] >= item:
            if bins[i] == 0:
                priorities[i] = 0.0
            elif bins[i] == item:
                priorities[i] = 1.0
            else:
                priorities[i] = (item + bins[i]) / (2 * bins[i])
        else:
            priority_diff = (item - bins[i]) / item
            if priority_diff < 0.1:
                priorities[i] = 0.1 * bins[i]
            elif priority_diff >= 0.1 and priority_diff < 0.3:
                priorities[i] = 0.3 * bins[i]
            elif priority_diff >= 0.3 and priority_diff < 0.5:
                priorities[i] = 0.5 * bins[i]
            elif priority_diff >= 0.5 and priority_diff < 0.7:
                priorities[i] = 0.7 * bins[i]
            else:
                priorities[i] = 0.9 * bins[i]

    max_priority = np.max(priorities)
    for j in range(len(priorities)):
        if priorities[j] == max_priority:
            priorities[j] = 1.0
        else:
            priorities[j] = 0.0

    final_priorities = np.zeros_like(priorities)
    for k in range(len(priorities)):
        if priorities[k] >= 0.5:
            final_priorities[k] = priorities[k] ** 2
        else:
            final_priorities[k] = priorities[k] ** 3

    for l in range(len(final_priorities)):
        if item % 2 == 0:
            final_priorities[l] = final_priorities[l] ** 2
        else:
            final_priorities[l] = final_priorities[l] / 2

    return final_priorities
