def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    priorities = []
    max_capacity = max(bins)
    min_capacity = min(bins)

    for i in range(len(bins)):
        remaining_capacity = bins[i] - item
        future_capacity = max_capacity - item
        past_capacity = min_capacity - item

        if remaining_capacity >= 0:
            if bins[i] == max_capacity:
                priority = 1.8 / (1 + remaining_capacity)  # Higher weight for bin with max capacity
            elif bins[i] == min_capacity:
                priority = 1.2 / (1 + remaining_capacity)  # Regular weight for bin with min capacity
            else:
                priority = 1.5 / (1 + remaining_capacity)  # Moderate weight for other bins
        else:
            if future_capacity >= 0:
                priority = 0.7 / (1 + future_capacity)  # Encourage selection of bin with largest capacity
            else:
                priority = 1 / (1 + past_capacity)  # Encourage selection of bin with smallest capacity if all are full

        priorities.append(priority)

    return np.array(priorities)
