def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    """Further improved version of `priority_v2`."""
    priorities = np.zeros_like(bins)

    if item < 0:
        raise ValueError("Item size cannot be negative")

    max_bin_capacity = np.max(bins)
    total_capacity = np.sum(bins)

    for i, bin_capacity in enumerate(bins):
        remaining_capacity = total_capacity - bin_capacity
        weighted_ratio = item / bin_capacity * remaining_capacity
        weighted_overflow = (item - bin_capacity) * (max_bin_capacity / (bin_capacity + 1))

        if bin_capacity <= 0:
            priorities[i] = np.nan
        else:
            if item <= bin_capacity:
                priorities[i] = min(weighted_ratio, 1.2 * weighted_overflow)  # Adding a cap to prioritize smaller bins
            else:
                if weighted_overflow > 0.1 * item:
                    priorities[i] = max(1.5 * weighted_overflow, 0.7 * weighted_ratio)  # Balancing prioritization
                else:
                    priorities[i] = min(0.8 * weighted_overflow, 1.2 * weighted_ratio)  # Handling overflow scenarios

    return priorities
