def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    priorities = []
    max_capacity = max(bins)

    for i in range(len(bins)):
        remaining_capacity = bins[i] - item
        future_capacity = max_capacity - item

        if remaining_capacity >= 0:
            if bins[i] == max_capacity:
                priority = 1.5 / (1 + remaining_capacity)  # Higher weight for bin with max capacity
            else:
                priority = 1 / (1 + remaining_capacity)
        else:
            priority = 0.5 / (1 + future_capacity)  # Encourage selection of bin with largest capacity

        priorities.append(priority)

    return np.array(priorities)
