def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    priorities = np.zeros_like(bins)  # Initialize priorities array

    if item < 0:
        raise ValueError("Item size cannot be negative")

    max_bin_capacity = np.max(bins)

    for i in range(len(bins)):
        if bins[i] <= 0:
            priorities[i] = np.nan  # Handle zero capacity bins
        else:
            if item <= bins[i]:
                priorities[i] = item / bins[i] * (max_bin_capacity - bins[i])  # Weighted ratio based on remaining capacity
            else:
                overflow = item - bins[i]
                total_bin_capacity = bins[i] + (max_bin_capacity - bins[i])  # Calculate total bin capacity

                priority_score = overflow * (max_bin_capacity / total_bin_capacity)  # Weighted overflow considering overall bin capacity

                if overflow > 0.5 * item:
                    priority_score *= 2  # Adjust priority based on overflow size
                elif overflow < 0.2 * item:
                    priority_score *= 0.5  # Adjust priority based on overflow size

                if bins[i] < 0.5 * max_bin_capacity:
                    priority_score *= 1.2  # Increase priority for small capacity bins
                elif bins[i] > 0.8 * max_bin_capacity:
                    priority_score *= 0.8  # Decrease priority for large capacity bins

                priorities[i] = priority_score

    return priorities
