def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    if item <= 0:
        raise ValueError("Item size must be greater than 0.")

    if len(bins) == 0:
        raise ValueError("Bins array must not be empty.")

    priorities = np.zeros_like(bins)

    # Calculate initial priorities based on bin capacities
    for i in range(len(bins)):
        if bins[i] <= 0:
            priorities[i] = np.inf
        else:
            priorities[i] = item / bins[i]

    # Apply additional scoring based on item and bin sizes
    for idx, val in enumerate(bins):
        if val < item:
            priorities[idx] *= 1.5
        elif val >= item and val < (2 * item):
            priorities[idx] *= 1.2
        else:
            priorities[idx] *= 0.8

    # Apply fine-tuning based on final priority values
    for idx, priority in enumerate(priorities):
        if np.isinf(priority):  # Handle infinite priorities
            priorities[idx] = 0.0
        else:
            priorities[idx] = np.sqrt(priority)  # Apply square root transformation

    return priorities
