def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    """Improved version of `priority_v1`."""
    if item <= 0:
        raise ValueError("Item size must be greater than 0.")

    if len(bins) == 0:
        raise ValueError("Bins array must not be empty.")

    priorities = np.zeros_like(bins)

    # Calculate initial priorities
    for i in range(len(bins)):
        if bins[i] <= 0:
            priorities[i] = np.inf
        else:
            priorities[i] = np.sqrt(item / bins[i]) + 0.5  # Adding a constant to prioritize smaller bins

    # Normalize priorities
    max_priority = np.max(priorities)
    min_priority = np.min(priorities)
    normalized_priorities = (priorities - min_priority) / (max_priority - min_priority)

    weighted_priorities = np.zeros_like(bins)

    # Adjust weights based on bin capacities
    for idx, val in enumerate(bins):
        if val < item:
            weighted_priorities[idx] = normalized_priorities[idx] * 1.5
        elif val >= item and val < 2*item:
            weighted_priorities[idx] = normalized_priorities[idx] * 1.2
        else:
            weighted_priorities[idx] = normalized_priorities[idx] * 0.8

    # Apply fine-tuning based on item size and priorities
    for i in range(len(bins)):
        if item < 5:
            weighted_priorities[i] *= 1.1
        elif item >= 5 and item < 10:
            weighted_priorities[i] *= 1.3
        else:
            weighted_priorities[i] *= 1.5

        # Adjust priorities based on neighboring bins
        if i > 0 and i < len(bins) - 1:
            if bins[i-1] < item and bins[i+1] < item:
                weighted_priorities[i] *= 1.3
            elif bins[i-1] < item or bins[i+1] < item:
                weighted_priorities[i] *= 1.1

    return weighted_priorities
