def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    if item <= 0:
        raise ValueError("Item size must be greater than 0.")

    if len(bins) == 0:
        raise ValueError("Bins array must not be empty.")

    priorities = np.zeros_like(bins)
    remaining_capacities = bins.copy()
    sorted_bins = np.flip(np.argsort(remaining_capacities))

    for i in sorted_bins:
        if remaining_capacities[i] <= 0:
            priorities[i] = np.inf
        else:
            priorities[i] = item / remaining_capacities[i]

    normalized_priorities = (priorities - np.min(priorities)) / (np.max(priorities) - np.min(priorities))

    weighted_priorities = np.sqrt(normalized_priorities)

    optimal_priorities = np.zeros_like(bins)

    for idx, val in enumerate(bins):
        if val < item:
            optimal_priorities[idx] = weighted_priorities[idx] * 1.5
        elif val >= item and val < (2 * item):
            optimal_priorities[idx] = weighted_priorities[idx] * 1.3
        else:
            optimal_priorities[idx] = weighted_priorities[idx] * 0.8

    return optimal_priorities
