def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    """Even more advanced version of `priority_v0` and `priority_v1`."""

    priorities = np.zeros_like(bins)  # Initialize priorities array

    for i in range(len(bins)):
        if bins[i] >= item:
            priorities[i] = 0  # If bin can accommodate the item, prioritize it with 0
        else:
            remaining_capacity = bins[i] - item  # Calculate remaining capacity after adding the item
            if remaining_capacity > 0:
                priorities[i] = 1 / remaining_capacity  # Prioritize based on the inverse of remaining capacity
            else:
                priorities[i] = np.inf  # If capacity is insufficient, prioritize with infinity

    return priorities
