def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    ratios = item / bins
    priorities = np.zeros(len(bins))

    for i in range(len(bins)):
        if ratios[i] > 0.9:
            if item > 15 and bins[i] > 10:
                priorities[i] = 6
            elif item > 15 and bins[i] <= 10:
                priorities[i] = 5
            else:
                priorities[i] = 4
        elif 0.6 <= ratios[i] <= 0.9:
            if item > 5 and bins[i] > 5:
                priorities[i] = 4
            elif bins[i] <= 5:
                priorities[i] = 3
            else:
                priorities[i] = 2
        else:
            if item < 5 and bins[i] > 3:
                priorities[i] = 2
            elif bins[i] <= 3:
                priorities[i] = 1
            else:
                priorities[i] = 0

    return priorities
