def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    if np.all(item <= bins):
        return np.zeros_like(bins)

    priorities = np.zeros_like(bins)
    max_bin_capacity = np.max(bins)

    for i in range(len(bins)):
        if item > bins[i]:
            priorities[i] = 2 * (item - bins[i])
            if bins[i] <= 0.3 * max_bin_capacity:
                priorities[i] += 1.2 * bins[i]
            if i % 3 == 0:
                priorities[i] += 2.3  # Additional priority for every 3rd bin
            elif i % 3 == 1:
                priorities[i] -= 1.3  # Reduced priority for every 3rd bin
            if bins[i] >= 0.8 * max_bin_capacity:
                priorities[i] += 3.5  # Extra high priority for bins close to full
            if bins[i] < 0.1 * max_bin_capacity:
                priorities[i] -= 2.7  # Reduced priority for nearly empty bins
        else:
            priorities[i] = (bins[i] - item) * 1.5  # Higher priority for not completely filling bins
            if bins[i] >= 0.7 * max_bin_capacity:
                priorities[i] -= 1.1 * (bins[i] - item)
            if i % 4 == 0:
                priorities[i] -= 1.5  # Reduced priority for every 4th bin
            elif i % 4 == 1:
                priorities[i] += 0.5  # Increased priority for every 4th bin
            if bins[i] < 0.3 * max_bin_capacity:
                priorities[i] -= 0.8  # Lower priority for bins with plenty of space
                if i % 2 == 0:
                    priorities[i] += 1.2  # Slightly increased priority for every other bin with plenty of space

    return priorities
