def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    if np.all(item <= bins):
        return np.zeros_like(bins)

    priorities = np.zeros_like(bins)
    max_bin_capacity = np.max(bins)

    for i in range(len(bins)):
        if item > bins[i]:
            priorities[i] = 2 * (item - bins[i])
            if bins[i] <= 0.3 * max_bin_capacity:
                priorities[i] += 1.2 * bins[i] * (1 + i/10)  # Increase priority for low capacity bins with incremental factor
            if i % 2 == 0:
                priorities[i] += 2.3 * (i + 1)  # Additional priority for every 2nd bin with incremental factor
            else:
                priorities[i] -= 1.3  # Reduced priority for other bins

        else:
            priorities[i] = bins[i] - item
            if bins[i] >= 0.7 * max_bin_capacity:
                priorities[i] -= 1.1 * (bins[i] - item)
            if i % 3 == 0:
                priorities[i] -= 1.5 * (i + 1)  # Reduced priority for every 3rd bin with incremental factor
            elif i % 3 == 1:
                priorities[i] += 0.5 * (i + 1)  # Increased priority for every 3rd bin with incremental factor

    return priorities
