def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    if item <= 0:
        raise ValueError("Item size must be greater than 0.")

    priorities = np.zeros_like(bins)
    for i in range(len(bins)):
        if bins[i] < item:
            priorities[i] = -1.0  # Not enough capacity, lowest priority
        else:
            remaining_capacity = bins[i] - item
            if remaining_capacity > 0:
                priorities[i] = 1.0 / (1.0 + remaining_capacity)  # Higher priority for more remaining capacity
            else:
                priorities[i] = 2.0  # Highest priority if capacity exactly matches item size
                continue  # Continue to next iteration instead of breaking

        if priorities[i] > 1.0:
            priorities[i] = 1.0  # Cap priority to 1.0
        elif priorities[i] < 0:
            priorities[i] = 0.0  # Set negative priorities to 0

    return priorities
