def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    ratios = item / bins
    priorities = np.zeros_like(bins)

    for i, ratio in enumerate(ratios):
        if ratio < 0.2:
            priorities[i] = 1 / (1 + np.exp(-15 * (ratio - 0.2))) + 0.2
        elif ratio >= 0.2 and ratio < 0.5:
            priorities[i] = 1 / (1 + np.exp(-7 * (ratio - 0.35))) + 0.3
        elif ratio >= 0.5 and ratio < 0.8:
            priorities[i] = 1 / (1 + np.exp(-5 * (ratio - 0.65))) + 0.4
        elif ratio >= 0.8 and ratio < 1:
            priorities[i] = 1 / (1 + np.exp(-3 * (ratio - 0.9))) + 0.5
        else:
            priorities[i] = 1 / (1 + np.exp(-2 * (ratio - 1.25))) + 0.6

        if i % 4 == 0:
            priorities[i] += 0.6
        elif i % 4 == 1:
            priorities[i] -= 0.4
        elif i % 4 == 2:
            priorities[i] += 0.2
        else:
            priorities[i] -= 0.2

    return priorities
