def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    priorities = np.zeros_like(bins)

    for i in range(len(bins)):
        if bins[i] >= item:
            if bins[i] == 0:
                priorities[i] = 0.0
            elif bins[i] == item:
                priorities[i] = 1.0
            else:
                priorities[i] = (item + bins[i]) / (2 * bins[i])
        else:
            capacity_ratio = bins[i] / item
            if capacity_ratio < 0.5:
                priorities[i] = 0.5
            else:
                priority_diff = (item - bins[i]) / item
                if priority_diff < 0.1:
                    priorities[i] = 0.2
                elif priority_diff >= 0.1 and priority_diff < 0.3:
                    priorities[i] = 0.4
                elif priority_diff >= 0.3 and priority_diff < 0.5:
                    priorities[i] = 0.6
                elif priority_diff >= 0.5 and priority_diff < 0.7:
                    priorities[i] = 0.8
                else:
                    priorities[i] = 1.0

    return priorities
