def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    """Even more improved version of priority function."""
    priorities = np.zeros_like(bins)

    for i in range(len(bins)):
        if bins[i] >= item:
            if bins[i] == 0:
                priorities[i] = 0.0
            elif bins[i] == item:
                priorities[i] = 1.0
            else:
                priorities[i] = (item + bins[i]) / (2 * bins[i])
        else:
            remaining_space_ratio = (item - bins[i]) / bins[i]
            if remaining_space_ratio < 0.1:
                priorities[i] = 0.3
            elif remaining_space_ratio >= 0.1 and remaining_space_ratio < 0.3:
                priorities[i] = 0.5
            elif remaining_space_ratio >= 0.3 and remaining_space_ratio < 0.5:
                priorities[i] = 0.7
            else:
                priority = 0.9
                for j in range(1, int(remaining_space_ratio) + 1):
                    priority -= 0.1
                priorities[i] = priority

    return priorities
