def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    priorities = np.zeros_like(bins)

    max_bin_capacity = np.max(bins)
    min_bin_capacity = np.min(bins)
    average_bin_capacity = np.mean(bins)

    for i in range(len(bins)):
        if item < bins[i]:
            priorities[i] = item / bins[i]
        else:
            priorities[i] = 1.0

        if bins[i] == max_bin_capacity:
            priorities[i] = priorities[i] + 0.2
        elif bins[i] == min_bin_capacity:
            priorities[i] = priorities[i] - 0.2
        else:
            priorities[i] = priorities[i] + 0.1

        if item > 1.5 * bins[i]:
            priorities[i] *= 1.3
        elif item > 1.2 * bins[i]:
            priorities[i] *= 1.2
        elif item > bins[i]:
            priorities[i] *= 1.1

    priorities = np.where(bins < average_bin_capacity, priorities * 1.2, priorities)

    return priorities
