def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    """Optimized version of `priority_v2`."""
    priorities = np.zeros_like(bins)  # Initialize priorities array

    if item < 0:
        raise ValueError("Item size cannot be negative")

    max_bin_capacity = np.max(bins)

    for i in range(len(bins)):
        current_bin_capacity = bins[i]

        if current_bin_capacity <= 0:
            priorities[i] = np.nan  # Handle zero capacity bins
        else:
            remaining_capacity = max_bin_capacity - current_bin_capacity

            if item <= current_bin_capacity:
                priorities[i] = item / current_bin_capacity * remaining_capacity  # Weighted ratio based on remaining capacity
            else:
                overflow = item - current_bin_capacity
                total_bin_capacity = current_bin_capacity + remaining_capacity

                if overflow >= 0.8 * item:
                    priority_score = overflow + (max_bin_capacity / total_bin_capacity)
                elif overflow >= 0.5 * item:
                    priority_score = overflow * 1.5 + (max_bin_capacity / total_bin_capacity)
                else:
                    priority_score = overflow * 0.8 + (max_bin_capacity / total_bin_capacity)

                priorities[i] = priority_score

    return priorities
