def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    """Enhanced version of `priority_v2`."""

    def calculate_priority(item: float, bin_capacity: float) -> float:
        if item <= bin_capacity / 2:
            return 1 + item / bin_capacity * 2
        elif item <= bin_capacity:
            return 1 + item / bin_capacity
        else:
            return 1 + np.log(item / bin_capacity)

    item_arr = np.full_like(bins, item)
    remaining_space = bins - item_arr

    priorities = np.zeros_like(bins)

    max_capacity = np.max(bins)
    min_capacity = np.min(bins)
    range_capacity = max_capacity - min_capacity

    for i in range(len(bins)):
        if bins[i] == max_capacity:
            priorities[i] = calculate_priority(item, bins[i]) + 2  # Increase priority for bins with maximum capacity
        elif bins[i] == min_capacity:
            priorities[i] = calculate_priority(item, bins[i]) - 1  # Decrease priority for bins with minimum capacity
        else:
            priorities[i] = calculate_priority(item, bins[i]) + (bins[i] - min_capacity) / range_capacity 

    return priorities
