def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    priorities = np.zeros_like(bins)

    for i in range(len(bins)):
        ratio = item / bins[i]

        if ratio < 0.4:
            priorities[i] = 2 * ratio + 0.5
        elif 0.4 <= ratio < 0.6:
            if bins[i] < 0.5 * sum(bins):
                priorities[i] = 1.7 * ratio + 0.8
            else:
                priorities[i] = 1.8 * ratio + 0.9
        elif 0.6 <= ratio < 0.8:
            priorities[i] = 1.6 * ratio + 1
        elif 0.8 <= ratio < 1:
            priorities[i] = 1.4 * ratio + 1.1
        elif ratio >= 1:
            priorities[i] = 1.2 * ratio + 1.2

    return priorities
