def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    ratios = item / bins
    priorities = np.zeros_like(bins)

    for i, ratio in enumerate(ratios):
        if ratio < 0.2:
            priorities[i] = 2.5 * ratio + 0.4
        elif ratio < 0.4:
            priorities[i] = 2.2 * ratio + 0.6
        elif ratio < 0.6:
            priorities[i] = 2 * ratio + 0.7
        elif ratio < 0.8:
            priorities[i] = 1.8 * ratio + 0.8
        elif ratio < 1:
            priorities[i] = 1.5 * ratio + 1
        elif ratio < 1.2:
            priorities[i] = 1.3 * ratio + 1.2
        elif ratio < 1.4:
            priorities[i] = 1.1 * ratio + 1.4
        else:
            priorities[i] = 1 * ratio + 1.5

    return priorities
