def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    """Complex priority function with optimized logic."""

    if item <= 0:
        raise ValueError("Item size must be greater than 0.")

    if len(bins) == 0:
        raise ValueError("Bins array must not be empty.")

    priorities = np.zeros_like(bins)

    # Calculate base priorities based on bin capacities
    for i in range(len(bins)):
        if bins[i] <= 0:
            priorities[i] = np.inf
        else:
            priorities[i] = item / bins[i]

    # Apply weight adjustments based on item size and bin capacities
    for idx, val in enumerate(bins):
        if val < item:
            weighted_priority = priorities[idx] * 1.5
        elif val >= item and val < (2 * item):
            weighted_priority = priorities[idx] * 1.2
        else:
            weighted_priority = priorities[idx] * 0.8

        # Adjust weighted priorities if necessary
        adjusted_priority = min(1, weighted_priority)
        priorities[idx] = adjusted_priority

    # Additional fine-tuning based on distribution of bin capacities
    sorted_bins = np.sort(bins)
    for idx, val in enumerate(sorted_bins):
        if val < item:
            priorities[idx] *= 1.1
        elif val >= item and val < (2 * item):
            priorities[idx] *= 1.05
        else:
            priorities[idx] *= 0.9

    return priorities
