def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    """Advanced version of priority function with optimized decision-making."""

    def calculate_priority(item: float, bin_capacity: float) -> float:
        if item <= 0.5 * bin_capacity:
            return 1 + item / bin_capacity * 2
        elif item <= 0.9 * bin_capacity:
            return 1 + item / bin_capacity
        else:
            return 1 + np.exp(item / bin_capacity)

    remaining_space = bins - item

    priorities = np.zeros_like(bins)

    for i in range(len(bins)):
        priorities[i] = calculate_priority(item, bins[i])

    sorted_indices = np.argsort(priorities)
    sorted_bins = bins[sorted_indices]

    for i in range(len(sorted_indices)):
        current_index = sorted_indices[i]
        if remaining_space[current_index] >= 0:
            break
        else:
            for j in range(len(sorted_indices)):
                nearest_empty_bin = sorted_indices[j]
                if remaining_space[nearest_empty_bin] >= item:
                    priorities[current_index] = 0
                    priorities[nearest_empty_bin] = 1
                    break

    return priorities
