def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    """Improved version of `priority_v1`."""
    priorities = np.zeros_like(bins)
    remaining_capacity = bins - item
    utilization_ratio = (bins - remaining_capacity) / bins

    for i in range(len(bins)):
        if utilization_ratio[i] <= 0.1:
            priorities[i] = 1.0
        elif utilization_ratio[i] <= 0.3:
            priorities[i] = 0.8
        elif utilization_ratio[i] <= 0.6:
            priorities[i] = 0.6
        elif utilization_ratio[i] <= 0.8:
            priorities[i] = 0.4
        else:
            priorities[i] = 0.2

        # Adjust priorities based on remaining capacity
        if bins[i] - item < 0.1 * bins[i]:
            priorities[i] -= 0.2
        elif bins[i] - item < 0.3 * bins[i]:
            priorities[i] -= 0.1

        # Compare utilization ratio with previous bin
        if i > 0:
            if utilization_ratio[i] > utilization_ratio[i-1]:
                priorities[i] += 0.1
            else:
                priorities[i] -= 0.1

        # Fine-tune priorities based on specific conditions
        if i % 2 == 0:  # If bin index is even
            priorities[i] += 0.2
        if bins[i] < 0.2 * item:  # If bin capacity is less than 20% of item size
            priorities[i] -= 0.3

    return priorities
