def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    """Improved version of `priority_v2`."""
    priorities = np.zeros_like(bins)

    for i in range(len(bins)):
        remaining_capacity = bins[i] - item
        utilization_ratio = (bins[i] - remaining_capacity) / bins[i]

        if remaining_capacity < 0.1 * item:
            priorities[i] = 0.1
        elif bins[i] < 1.5 * item:
            priorities[i] = max(0.1, priorities[i] - 0.2)

        if utilization_ratio < 0.2:
            priorities[i] = 0.9
        elif 0.2 <= utilization_ratio < 0.5:
            priorities[i] = 0.7
        elif 0.5 <= utilization_ratio < 0.8:
            priorities[i] = 0.5
        elif 0.8 <= utilization_ratio < 0.9:
            priorities[i] = 0.3
        else:
            priorities[i] = 0.1

    return priorities
