def priority(item: float, bins: np.ndarray) -> np.ndarray:
    """Returns priority with which we want to add item to each bin.

    Args:
        item: Size of item to be added to the bin.
        bins: Array of capacities for each bin.

    Return:
        Array of same size as bins with priority score of each bin.
    """
    if item <= 0:
        raise ValueError("Item size must be greater than 0.")

    if len(bins) == 0:
        raise ValueError("Bins array must not be empty.")

    priorities = np.zeros_like(bins)

    # Calculate priorities based on bin capacities and item size
    for i in range(len(bins)):
        if bins[i] <= 0:
            priorities[i] = np.inf
        else:
            priorities[i] = item / bins[i]

    # Apply non-linear weighting to priorities based on bin capacities
    for idx, val in enumerate(bins):
        if val < item:
            weighted_priority = (priorities[idx] ** 2) * 1.5
            normalized_priorities[idx] = weighted_priority
        elif val >= item and val < (2 * item):
            weighted_priority = (priorities[idx] ** 1.5) * 1.3
            normalized_priorities[idx] = weighted_priority
        else:
            weighted_priority = (priorities[idx] ** 1.2) * 0.7
            normalized_priorities[idx] = weighted_priority

    # Apply additional fine-tuning based on distribution of bin capacities
    sorted_bins = np.sort(bins)
    for idx, val in enumerate(sorted_bins):
        if val < item:
            normalized_priorities[idx] += 0.2
        elif val >= item and val < (2 * item):
            normalized_priorities[idx] += 0.1
        else:
            normalized_priorities[idx] -= 0.15

    return normalized_priorities
